mapper.select:
SELECT id, tenant_id, code, name, resource, action, description, created_at, updated_at
FROM permission
WHERE id = #{id}

SELECT id, tenant_id, code, name, resource, action, description, created_at, updated_at
FROM permission
WHERE code = #{code}
  AND tenant_id = #{tenantId}

SELECT id, tenant_id, code, name, resource, action, description, created_at, updated_at
FROM permission
WHERE resource = #{resource}
  AND tenant_id = #{tenantId}
ORDER BY id ASC

SELECT COUNT(*) > 0
FROM permission
WHERE code = #{code}
  AND tenant_id = #{tenantId}
