mapper.select:
SELECT id, tenant_id, code, name, description, status, created_at, updated_at
FROM role
WHERE id = #{id}

SELECT id, tenant_id, code, name, description, status, created_at, updated_at
FROM role
WHERE code = #{code}
  AND tenant_id = #{tenantId}

SELECT COUNT(*) > 0
FROM role
WHERE code = #{code}
  AND tenant_id = #{tenantId}

SELECT permission_id
FROM role_permission
WHERE role_id = #{roleId}
