SELECT id, tenant_id, username, password, email, phone, nickname, avatar,
       status, email_verified, last_login_at, created_at, updated_at
FROM user
WHERE username = #{username}
  AND tenant_id = #{tenantId}

SELECT id, tenant_id, username, password, email, phone, nickname, avatar,
       status, email_verified, last_login_at, created_at, updated_at
FROM user
WHERE email = #{email}
  AND tenant_id = #{tenantId}

SELECT COUNT(*) > 0
FROM user
WHERE username = #{username}
  AND tenant_id = #{tenantId}

SELECT COUNT(*) > 0
FROM user
WHERE email = #{email}
  AND tenant_id = #{tenantId}

SELECT COUNT(*)
FROM user
WHERE tenant_id = #{tenantId}

SELECT role_id
FROM user_role
WHERE user_id = #{userId}

SELECT id, tenant_id, code, name, description, status, created_at, updated_at
FROM role
WHERE code = #{code}
  AND tenant_id = #{tenantId}
